INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (145, 'SED,CFI,PRG', 'SD06', NULL, 37, N'02   ', 'Accreditation', 'CredentialingAssessmentInfo', 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item1-1C. (formerly 1-1D.). Screenings/Assessments to determine eligibility for services occur either prenatally or within the first two weeks after the birth of the baby.', '1-1.D Assessment Information (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (146, 'SED', 'SD12', NULL, 54, N'03   ', 'Accreditation', NULL, 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 1-2 A and B, measuring acceptance rates and "refusal" rates for particiants with positive Kempe scores.', '1-2.A and B Kempe Analysis')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (147, 'SED,CFI,PRG', 'SD12', NULL, 38, N'05   ', 'Accreditation', 'FirstHomeVisit', 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 1-1F. (formerly 1-3). The program ensures that, for those who accept home visitor services, the first home visit occurs prenatally or within the first three months after the birth of the baby.', '1-3 Timing of First Home Visit (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (148, NULL, NULL, NULL, 59, N'06   ', 'Accreditation', NULL, 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 2-2 B. (formerly 2-2C.). This report lists all current staff who have administered a Kempe Assessment, the date of their Kempe Training (FAW Core Training) and the date of the first Kempe Assessment they administered.', '2-2.C Kempe Training')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (149, 'SED,CFI,PRG', 'SD12', NULL, 40, N'07   ', 'Accreditation', 'CreativeOutreach', 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 3-3B. (formerly 3-3C.). The program places participants in outreach appropriately and continues outreach for three months, only concluding outreach prior to three months when the participants have (re)engaged in services, refused services or moved from the area', '3-3.C Use of Creative Outreach (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (150, 'SER,PRG', 'SD-24,24', 'RetentionRates', 58, N'08   ', 'Accreditation', NULL, 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 3-4 A and B. This report analyzes retention rates for enrolled participants, the reasons for their discharge and the demographics of this cohort, both at intake and closure. Closure is defined as the date of the last actual home visit.', '3-4.A and B Retention Rate Analysis (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (153, 'CFI,PRG', NULL, NULL, 39, N'12   ', 'Accreditation', '3YearsInPrgm', 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 4-3 (formerly 4-3B.). The program ensures that it offers home visitation services to participants for a minimum of three years after the birth of the baby (for those participants who wish to continue participating).', '4-3.B Participant in Program for at Least 3 Years as of Today (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (154, NULL, NULL, NULL, 51, N'13   ', 'Accreditation', NULL, 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 6-5 (formerly 6-6.). Those who administer developmental screenings have been trained in the use of the tool before administering it.', '6-6 ASQ Training')
