INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (156, 'WC0', NULL, NULL, 42, N'50   ', 'Accreditation', NULL, 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 10-2. Staff (assessment workers, home visitors, and supervisors) receive orientation (separate from intensive role specific training) prior to direct work with children and families to familiarize them with the functions of the program.', '10-2 Orientation Training')
