INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (159, 'WC0', NULL, NULL, 45, N'53   ', 'Accreditation', NULL, 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 10-5. Staff (assessment workers, home visitors, and supervisors) demonstrate knowledge on a variety of topics necessary for effectively working with families and children with twelve months of hire.', '10-5 Demonstrated Knowledge Training for Staff by Twelve Months')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (160, 'SED', 'SD12', NULL, 46, N'54   ', 'Accreditation', NULL, 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 10-6. The program ensures that all program staff receive ongoing training which takes into account the worker''s knowledge and skill base.', '10-6 On-Going Training')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (161, 'SED', 'SD12', NULL, 47, N'55   ', 'Accreditation', NULL, 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 5-3. The program provides staff training on culturally sensitive practices based on the unique characteristics of population(s) being served (i.e. , age related factors, language, culture, etc.) by the program.', '5-3 Culturally Sensitive Practices')
