INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (165, 'CFI,PRG', NULL, NULL, 56, N'59   ', 'Accreditation', NULL, 'Pre-Site visit evidence to address HFA Credentialing Site Self-Assessment Tool item 11-2 A, a report listing the last 5 visits for each active FSW where the Supervisor was present. NYS policy requires four home visits observations per year by the supervisor for each FSW. 11-2b, Kempe Observation by Supervisor is similiar to 11-2A, but lists last five FAW supervisor observations of each active FAW.', '11-2.A-B Home Visit and Kempe Observation by Supervisor (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (166, 'WC2', NULL, NULL, 66, N'60   ', 'Accreditation', NULL, 'NYS4 "No Home Visits Before FSW Core Training" lists all active FSWs with their date of first Home Visit and date of FSW core training. This report is designed to ensure all FSW''s are fully trained before visiting with families.', 'NYS4 No Home Visits before FSW Core Training')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (167, 'SED', 'SD01', NULL, 8, N'A    ', 'Lists', NULL, 'The Data Report provides a summary of program activity for a specified period of time. Screens, Pre-Assessment activities, Kempe Assessments, Pre-Intake activities and Enrolled Cases are displayed for use in management reports or as information to other interested parties. ', 'Data Report')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (168, 'WKA', NULL, NULL, 5, N'B    ', 'Lists', 'Facesheet', 'The Face Sheet summaries information about the case, the PC1, the PC2, the Contact Person and the Target Child. It also lists current information about medical insurance, public benefits and medical providers.', 'Face Sheet')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (169, 'MYO', 'SD01', NULL, 11, N'C    ', 'Analysis', 'FAWMonthlyReport', 'The FAW Monthly report summarizes FAW caseload information and their monthly activity. It is used for supervision and worker validation purposes.', 'FAW Monthly Report')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (170, 'MYO,WKN', 'SD01', NULL, 15, N'D    ', 'Analysis', 'PreIntakeActivitySummary', 'The Pre-Intake Activity Summary is a management report used to evaluate efforts being made to engage participants by Family Support Workers.', 'Preintake Activity Summary')
