INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (172, 'SED', 'SD01', NULL, 12, N'F    ', 'Lists', NULL, 'The Summary of Referral Sources report lists the number of times referral sources appear on the HFNY Screen form for any period of time.', 'Summary of Referral Sources')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (173, 'SED,CFI,PRG', 'SD03', NULL, 17, N'G    ', 'Analysis', 'NYSFSWHomeVisitRecord_Summary', 'The NYS FSW Home Visit Record measures the FSW''s home visit output and compares it to the expected amount of visits for the Report Period.', 'NYS Family Support Worker Home Visit Record - Summary (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (174, 'SED,CFI,PRG', 'SD01', NULL, 30, N'H    ', 'Analysis', 'ProgramDemographics', 'The Program Dempgraphics report summarizes characteristics for participants served over a specified period of time. Included in this summary are total participants, race/ethnicity of PC1, Age of PC1 at enrollment, education of PC1 at enrollment, employment and training at enrollment, families receiving public benefits at enrollment, PC1 married at enrollment, household composition at enrollment, first time mothers at enrollment, length of program participation, prenatal at enrollment and primary caretaker needs interperter for English.', 'Program Demographics (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (175, 'WKA,AUC,CFI,PRG', NULL, NULL, 31, N'I    ', 'Lists', 'ASQHistory', 'Provides a history of ASQ scores for each participant by FSW caseload.There is an option to print this list for only cases with under cutoff scores.', 'ASQ History (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (176, 'WKA,PUC,CFI,PRG', NULL, NULL, 32, N'J    ', 'Lists', 'PSIHistory', 'Provides a history of PSI scores for each participant by FSW caseload. There is an option to print a list of only cases with scores above the 85th percentile.', 'PSI History (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (177, 'SED,CFI,PRG', 'SD01', NULL, 33, N'K    ', 'Analysis', 'KempePC1Issues', 'This report provides a list of all PC1''s who have a Domestic Violence, Substance/Alcohol Abuse and/or Mental Health Issues that have been identified in the Kempe Current Issues. It also lists if there is a Service Referral made for that particular issue(s) within six months of the Intake Date.', 'Kempe PC1 Issues (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (178, 'SED,CFI,PRG', 'SD01', NULL, 35, N'L    ', 'Lists', NULL, 'The Program Synopsis is used as a monthly summary of activity for the program. It can be run for any time period as well. Screens, Kempes, enrollment, referrals, home visits and form information give the user a quick management look at program status.', 'Program Synopsis (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (179, 'WKA', NULL, NULL, 52, N'M    ', 'Lists', 'LevelChangeHistory', 'Allows for viewing and printing of one participant''s level change history: PC1_ID, Level, Date Level Assigned, and Days on Level.', 'Level Change History Report')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (180, 'SED,WKN', 'SD12', NULL, 62, N'N    ', 'Analysis', 'ScreenReferralSourceOutcomeSummary', 'Screen/Referral Source Outcome Summary lists individual referral sources during a time period and the corresponding ages of the target children at the time of the referrals and the current outcomes of those referrals. The same analysis is provided for all referral sources.', 'Screen Referral Source Outcome Summary')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (181, 'SED,WKN,RSB', 'SD12', NULL, 64, N'O    ', 'Lists', NULL, 'The Screen/Referral Source Demographic and Outcome Analysis is a more indepth analysis of screens than the Screen/Referral Source Outcome Summary . The user has a choice between one referral source or all referral sources and the analysis is done on expectant parent type, age of expectant parent, timing of screening, type of referral, risk factors and reasons not referred.', 'Screen/Referral Source Demographic and Outcome Analysis')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (182, 'QTR,WKA', 'LFQ', NULL, 70, N'P    ', 'Lists', NULL, 'Summary of Unique Families by Type of Home Visit Activities is similiar to the Summary of Home Visit Log Report on this tab. However, instead of aggregating activities and visits, this report counts unique families visited in the period and counts the number of families that receive each kind of activity listed on the Home Visit Log. It can be run for the whole program or by worker.', 'Summary of Unique Families by Type of Home Visit Activities')
