INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (188, 'QTR,WKN,ICC,CFI,PRG', 'LFQ', NULL, 19, N'A    ', 'Quarterlies', NULL, 'Performance Targets display the program''s achievement rate in the areas of Health and Development, Parent Child Interaction and Maternal Life Course.This report also lists participants who have not meet a specific target and lists participants where the case data quality prevents measurement of a specific target. This report can be run for all cases for one worker.', 'Performance Targets (Case Filter/Site Options)')
