INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (190, 'QTR,ICC,CFI,PRG', 'LFQ', NULL, 20, N'C    ', 'Quarterlies', 'PreAssessEngagement', 'Pre-Assessment Engagement Report summarizes activity for the report period related to screens and the program''s ability to assess participants once they score positive on the screen.', 'Quarterly Pre-Assessment Engagement (Case Filter/Site Options)')
