INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (192, 'QTR,ICC,CFI,PRG', 'LFQ', NULL, 25, N'E    ', 'Quarterlies', NULL, 'A broad variety of characteristics are summarized for the families served during the report period. Characteristics are seen from two points of view: what the families looked like at enrollment and what the same cohort looks like as of the end of the report or currently.', 'Family Characteristics (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (193, 'QTR,ICC,CFI,PRG', 'LFQ', NULL, 23, N'F    ', 'Quarterlies', NULL, 'The Service Referral component of the Quarterly report summarizes referrals and their outcomes from the beginning of the current contract to the end date of the quarter. The referrals are broken down by those arranged for the participant by the home visitor and those where information was given to the participant to follow through themselves.', 'Service Referrals (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (194, 'QTR,ICC,CFI,PRG', 'LFQ', NULL, 28, N'G    ', 'Quarterlies', 'HomeVisitLogSummaryQuarterly', 'The Home Visit Log Summary aggregates information from the Home Visit Log for the quarter and contract to date.', 'Quarterly Home Visits Log Summary (Case Filter/Site Options)')
