INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (199, 'QTR,WKN,ICC,CFI,PRG', 'LFQ', NULL, 36, N'L    ', 'Quarterlies', NULL, 'Performance Targets displays four quarters of the program''s achievement rate in the areas of Health and Development, Parent Child Interaction and Maternal Life Course.', 'Performance Targets for 4 Quarters (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (200, 'QTR,ICC,CFI,PRG', 'LFQ', NULL, 60, N'M    ', 'Quarterlies', NULL, 'The Training Report Summary gives a chronilogical list of trainings during the report period. Date of training, title of training, training description, HFA training codes, length of time for each code, cultural competency and staff participanting are included.', 'Training Report Summary (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (201, 'QTR,ICC,CFI,PRG', 'LFQ', NULL, 61, N'N    ', 'Quarterlies', NULL, 'The HFA Home Visit Record measures the FSW''s home visit output and compares it to the expected amount of visits required by Healthy Families America guidelines. It is used both for supervision and worker validation.', 'HFA Home Visit Record (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (202, 'CMO,WKA', 'SD00', NULL, 6, N'A    ', 'Ticklers', NULL, 'The FSW Tickler provides the program staff with both a schedule of upcoming events and a form history of the case. Be patient when running this report as it may take a few seconds. This report should be run monthly.', 'FSW Enrolled Case Tickler')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (203, 'CMO,WKA', 'SD00', NULL, 7, N'B    ', 'Ticklers', 'FSWPreIntakeTickler', 'The FSW Pre-Intake Tickler should be run at the same time the FSW Tickler is run. It lists all pre-intake cases by worker. It lists the PC1 ID, the participant name, phone, Days since FSW assignment, Enrollment Age out date and Date of the last Pre-Intake form.', 'FSW Pre-Intake Case Tickler')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (204, 'CMO,WKN,TIC', 'SD00', 'FSWEnrolledCaseTicklerSummaries', 16, N'C    ', 'Ticklers', 'FSWEnrolledCaseTicklerSummaries', 'The FSW Enrolled Case Tickler Summaries can be used by both Supervisors and FSW''s to list cases with forms coming due. This report option allows the user to view or print ticklers by form: ASQ, PSI, Follow-up, IFSP or a combined tickler that includes ASQ, PSI, Follow-up, IFSP and Lead Assessment grouped by worker for each case.', 'FSW Enrolled Case Tickler Summaries')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (205, 'FAW,FTS', NULL, NULL, 4, N'D    ', 'Ticklers', 'FAWTickler', 'The FAW Tickler lists all cases that have screened positive, been referred for an assessment and have not been assessed or terminated. The elements of the report are PC1 ID, Name, Phone, Screen date, Last Recorded pre/post-Natal status, EDC or DOB, Enrollment Age out Date, Assessment Critical Element Target Date, and Date of Last Pre-Assessment Form. This report should be run monthly.', 'FAW Tickler')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (206, 'WKA', NULL, 'ServiceReferralsNeedFollowUp', 9, N'E    ', 'Ticklers', NULL, 'The Service Referrals Needing Follow-up Report lists referrals by case and by worker that have been data entered without an outcome. This list is used as a reminder to FSW''s to check with their families for referral outcomes.', 'Service Referrals Needing Follow-up')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (207, 'SUP', NULL, NULL, 2, N'F    ', 'Ticklers', NULL, 'The Supervisor''s Case List is a summary of family support worker caseloads organized by supervisor. It includes case weights, the total number of cases and the number of cases on each level.', 'Supervisor''s Case List')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (208, 'WKN', NULL, NULL, 3, N'G    ', 'Ticklers', NULL, 'The FSW Case List displays cases for each FSW with helpful information such as Name, PC1 ID, address, phone number, Target Child Name or EDC, current level and current level start date. It also sums the number of Active cases, the number of Pre-Intake cases and the total case weight. This report should be run monthly and given to the FSW to check for accuracy.', 'FSW Case List')
