INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (167, 'SED,PRG', 'SD01', NULL, 8, N'A    ', 'Analysis', 'DataReport', 'The Data Report provides a summary of program activity for a specified period of time. Screens, Pre-Assessment activities, Parent Surveys, Pre-Intake activities and Enrolled Cases are displayed for use in management reports or as information to other interested parties. ', 'Data Report')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (168, 'WKL', NULL, NULL, 5, N'B    ', 'Lists', 'Facesheet', 'The Face Sheet summaries information about the case, PC1, OBP, PC2, Contact Person and the Target Child. It also lists current information about medical insurance, public benefits and medical providers.', 'Face Sheet')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (169, 'MYO', 'SD01', NULL, 11, N'C    ', 'Analysis', 'FAWMonthlyReport', 'The FRS Monthly report summarizes FRS caseload information and their monthly activity. It is used for supervision and worker validation purposes.', 'FRS Monthly Report')
