INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (171, 'SED,CFI,HVB', 'SD06', NULL, 10, N'E    ', 'Analysis', 'HomeVisitLogActivitySummary', 'The Home Visit Log Summary aggregates information mostly in the form of percentages from the Home Visit Log for use in supervision and for worker validation.', 'Summary of Home Visit Log Activities (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (173, 'SED,CFI', 'SD03', 'Quarterly', 17, N'G    ', 'Analysis', 'NYSFSWHomeVisitRecord_Summary', 'The FSS Home Visit Record summary report measures the FSSs home visit output and compares it to the expected amount of visits for the Report Period on a worker level.', 'Family Support Specialist Home Visit Record - Summary (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (174, 'SED,CFI', 'SD01', 'Annual', 30, N'H    ', 'Analysis', 'ProgramDemographics', 'The Program Demographics report summarizes characteristics for participants served over a specified period of time. Included in this summary are total participants, race/ethnicity of PC1, Age of PC1 at enrollment, education of PC1 at enrollment, employment and training at enrollment, families receiving public benefits at enrollment, PC1 married at enrollment, household composition at enrollment, first time mothers at enrollment, length of program participation, prenatal at enrollment and primary caretaker needs interpreter for English.', 'Program Demographics (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (175, 'WKL,AUC,CFI', NULL, NULL, 31, N'I    ', 'Lists', 'ASQHistory', 'A history of ASQ scores for each target child by Supervisor, FSS or PC1 ID. There is an option to print this list for only cases with under cutoff scores.', 'ASQ History (Case Filter/Site Options)')
