INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (177, 'SED,CFI', 'SD06', NULL, 33, N'K    ', 'Analysis', 'KempePC1Issues', 'HFNY Multi-site policy evidence to address requirement of receiving FGP/IFSP training within three months of hire', 'Parent Survey PC1 Issues (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (178, 'SED,CFI', 'SD01', NULL, 35, N'L    ', 'Analysis', 'ProgramSynopsis', 'The Program Synopsis is best used as a monthly summary of activity for the program. It can be run for any time period as well. Screens, Parent Surveys, enrollment, referrals, home visits and form information give the user a quick management look at program status.', 'Program Synopsis (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (179, 'WKL', NULL, NULL, 52, N'M    ', 'Lists', 'LevelChangeHistory', 'Allows for viewing and printing of one participants level change history: PC1_ID, Level, Date Level Assigned, and Days on Level.', 'Level Change History Report')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (180, 'SED', 'SD12', 'Annual', 62, N'N    ', 'Analysis', 'ScreenReferralSourceOutcomeSummary', 'Screen/Referral Source Outcome Summary lists individual referral sources during a time period and outcomes of positive screens. Outcomes range from pending assessment to enrollment. The same analysis is provided for all referral sources.', 'Screen Referral Source Outcome Summary')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (181, 'SED,RSB,PRG', 'SD12', NULL, 64, N'O    ', 'Analysis', 'ScreenReferralSourceDemographicsAndOutcomeAnalysis', 'The Screen/Referral Source Demographic and Outcome Analysis is a more in-depth analysis of screens than the Screen/Referral Source Summary. The user has a choice between one referral source or all referral sources and the analysis is done on expectant parent type, age of expectant parent, timing of screening, race/ethnicity, target area, OBP in home, receiving prenatal care, type of referral, risk factors and reasons not referred in a time frame.', 'Screen/Referral Source Demographic and Outcome Analysis')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (185, 'SRB,CFI,SED,PRG', 'SD03', 'ServiceReferral_Code', 71, N'R    ', 'Lists', 'ServiceReferralByCode', 'This list shows a breakdown of the kind of referrals made over a period of time.', 'Count of Service Referrals by Code (Case Filter/Site Options)')
