INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (188, 'QTR,WKF,ICC,PTO,CFI', 'LFQ', 'Quarterly, Annual', 19, N'A    ', 'Quarterlies', 'PerformanceTargetReport', 'Performance Targets display the programs achievement rate in the areas of Health and Development, Parent Child Interaction and Family Life Course. This report has optional reports that list participants who have met target, have not met a specific target and participants where the case data quality prevents measurement of a specific target. This report can be run for all cases for one worker.', 'Quarterly Performance Targets (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (189, 'QTR,CFI', 'LFQ', 'Quarterly', 22, N'B    ', 'Quarterlies', 'EnrolledProgramCaseload', 'The Enrolled Program Caseload Information report summarizes the activity of the enrolled cohort for the time period. Numbers related to families enrolled at the beginning of the period, new families enrolled during the period, families discharged during the period and active enrolled cases at the end of the period are displayed. A summary of the discharge reasons, visits per month by level and a list of enrolled participants at the end of the period are also part of this report.', 'Quarterly Enrolled Program Caseload Information (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (190, 'QTR', 'LFQ', 'Quarterly, Annual', 20, N'C    ', 'Quarterlies', 'PreAssessEngagement', 'Pre-Assessment Engagement Report summarizes activity for the report period prior to assessment and the programs ability to assess participants once they score positive on the screen.', 'Quarterly Pre-Assessment Engagement')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (191, 'QTR', 'LFQ', 'Quarterly, Annual', 21, N'D    ', 'Quarterlies', 'PreIntakeEngagement', 'Pre-Intake Engagement Report summarizes activity for the report period related to Parent Surveys and the programs ability to enroll participants once they score positive on the Parent Survey.', 'Quarterly Pre-Intake Engagement')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (194, 'QTR,CFI', 'LFQ', 'Quarterly', 28, N'G    ', 'Quarterlies', 'HomeVisitLogSummaryQuarterly', 'The Home Visit Log Summary aggregates information from the Home Visit Log for the quarter and contract to date.', 'Quarterly Home Visits Log Summary (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (195, 'QTR,CFI,PRG', 'LFQ', 'Quarterly', 29, N'H    ', 'Quarterlies', 'PrimaryCaretaker1Issues', 'This quarterly report compares primary caregiver one issues at enrollment or Parent Survey to the most current record of issues. ', 'Quarterly Primary Caregiver 1 Issues (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (197, 'QTR,CFI', 'LFQ', 'Quarterly, Indicator', 24, N'J    ', 'Quarterlies', 'ProgramInformationFor8Quarters', 'The Quarterly Program Information report displays eight quarters of program data for managers, contract managers, quality assurance persons and program staff.', 'Quarterly Program Information for 8 quarters (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (198, 'QTR,SIT,PRG', 'LFQ', 'Quarterly, Annual', 27, N'K    ', 'Quarterlies', 'WorkerCharacteristicsSummary', 'The Worker Characteristic Summary report has two parts: 1. An aggregate table of worker characteristics collected on the Worker Information at Hire form broken down by job category,  and 2. An individual listing of each worker used in constructing the worker characteristic table.', 'Quarterly Worker Characteristics Summary (Site Option)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (199, 'QTR,WKN,ICC,CFI', 'LFQ', 'Quarterly, Annual, Indicator', 36, N'L    ', 'Quarterlies', 'PerformanceTargetReport4Quarters', 'Performance Targets displays four quarters of the programs achievement rate in the areas of Health and Development, Parent Child Interaction and Family Life Course.', 'Quarterly Performance Targets for 4 Quarters (Case Filter/Site Options)')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (202, 'CMO,WKL', 'SD00', 'FSWEnrolledCaseTickler', 6, N'A    ', 'Ticklers', 'FSWEnrolledCaseTickler', 'The FSS Tickler provides the program staff with both a schedule of upcoming events and a form history of the case. This report should be run monthly.', 'FSS Enrolled Case Tickler')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (203, 'N/A', 'SD00', NULL, 7, N'B    ', 'Ticklers', 'FSWPreIntakeTickler', 'The FSS Pre-Intake Tickler should be run at the same time the FSS Tickler is run. It lists all pre-intake cases by worker. It lists the PC1 ID, the participant name, phone, Days since FSS assignment, Enrollment Age out date and Date of the last Pre-Intake form.', 'FSS Pre-Intake Case Tickler')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (204, 'CMO,WKN,TIC', 'SD00', 'FSWEnrolledCaseTicklerSummaries', 16, N'C    ', 'Ticklers', 'FSWEnrolledCaseTicklerSummaries', 'The FSS Enrolled Case Tickler Summaries can be used by both Supervisors and FSSs to list cases where forms are coming due. This report option allows the user to view or print ticklers by form: ASQ, PSI, Follow-up, or a combined tickler that includes ASQ, PSI, and Follow-up grouped by worker.', 'FSS Enrolled Case Tickler Summaries')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (205, 'FAW,FTS', NULL, NULL, 4, N'D    ', 'Ticklers', 'FAWTickler', 'The FRS Tickler lists all cases that have screened positive, been referred for an assessment and have not been assessed or terminated. The elements of the report are PC1 ID, Name, Phone, Screen date, Last Recorded pre/post-Natal status, EDC or DOB, Enrollment Age out Date, Assessment Critical Element Target Date, and Date of Last Pre-Assessment Form. This report should be run at least monthly.', 'FRS Tickler')
INSERT INTO [dbo].[codeReportCatalog] ([codeReportCatalogPK], [CriteriaOptions], [Defaults], [Keywords], [OldReportFK], [OldReportID], [ReportCategory], [ReportClass], [ReportDescription], [ReportName]) VALUES (209, 'SED,CFI,WKF', 'SD01', NULL, 13, N'H    ', 'Lists', 'ActiveEnrolledCaseList', 'The Active Enrolled Case list is used to view or print a list of participants and key attributes who are active during a period of time.', 'Active Enrolled Case List (Case Filter/Site Options)')
